const Precio = (props) => {
  console.log("TCL: Precio -> props", props)
  const { price, market_cap, percent_change_1h, percent_change_7d, percent_change_24h } = props.precio;
  console.log("TCL: Precio -> price", price)
  return (
    <div className="card text-white bg-success mb-3">
      <div className="card-header">
        Precio del Bitcoin
        </div>
      <div className="card-body">
        <h4 className="card-title">Precio Actual: ${Math.round(price * 100) / 100}</h4>
        <div>
  <p className="card-text"><span className="font-weight-bold">Última Hora: {percent_change_1h}</span></p>
  <p className="card-text"><span className="font-weight-bold">Últimas 24 Horas: {percent_change_7d}</span></p>
  <p className="card-text"><span className="font-weight-bold">Últimos 7 Días: {percent_change_24h}</span></p>
        </div>
      </div>
    </div>
  );
}
export default Precio;
